import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Main() {
  const navigate = useNavigate();
  const [isSearchVisible, setIsSearchVisible] = useState(true);

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 768) {
        setIsSearchVisible(false);
      } else {
        setIsSearchVisible(true);
      }
    };

    window.addEventListener('resize', handleResize);
    handleResize(); // Initial check
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const MainContent = () => {
    const movieCovers = [
      { id: 1, image: 'MV5BYTJkNGQyZDgtZDQ0NC00MDM0LWEzZWQtYzUzZDEwMDljZWNjXkEyXkFqcGc@._V1_.jpg' },
      { id: 2, image: 'MV5BYTJkNGQyZDgtZDQ0NC00MDM0LWEzZWQtYzUzZDEwMDljZWNjXkEyXkFqcGc@._V1_.jpg' },
      { id: 3, image: 'MV5BYTJkNGQyZDgtZDQ0NC00MDM0LWEzZWQtYzUzZDEwMDljZWNjXkEyXkFqcGc@._V1_.jpg' },
      { id: 4, image: 'MV5BYTJkNGQyZDgtZDQ0NC00MDM0LWEzZWQtYzUzZDEwMDljZWNjXkEyXkFqcGc@._V1_.jpg' },
    ];

    const genres = [
      { id: 1, title: 'Film', icon: `${process.env.PUBLIC_URL}/icons8-film-reel-30 1.png` },
      { id: 2, title: 'Series', icon: `${process.env.PUBLIC_URL}/icons8-xbox-series-x-30 1.png` },
      { id: 3, title: 'Reality', icon: `${process.env.PUBLIC_URL}/icons8-cinema-24(2) 1.png` },
      { id: 4, title: 'Games', icon: `${process.env.PUBLIC_URL}/icons8-game-30 1.png` },
      { id: 5, title: 'Documentary', icon: `${process.env.PUBLIC_URL}/icons8-documentary-30 1.png` },
      { id: 6, title: 'Stand-up Comedy', icon: `${process.env.PUBLIC_URL}/icons8-mic-30 1.png` },
    ];

    return (
      <div>
        {/* Trending Section */}
        <h2 className="text-white text-4xl font-jeju-hallasan text-center mt-20 mb-10">Trending</h2>
        <div className="flex flex-wrap justify-center gap-8">
          {movieCovers.slice(0, window.innerWidth < 768 ? 3 : 4).map((movie) => (
            <div
              key={movie.id}
              className="w-48 h-72 bg-cover bg-center cursor-pointer"
              style={{ backgroundImage: `url(${process.env.PUBLIC_URL}/movie.png)` }}
              onClick={() => console.log(`Clicked on movie ${movie.id}`)}
            />
          ))}
        </div>

        {/* Most Viewed Section */}
        <h2 className="text-white text-4xl font-jeju-hallasan text-center mt-20 mb-10">Most Viewed</h2>
        <div className="flex flex-wrap justify-center gap-8">
          {movieCovers.slice(0, window.innerWidth < 768 ? 3 : 4).map((movie) => (
            <div
              key={movie.id}
              className="w-48 h-72 bg-cover bg-center cursor-pointer"
              style={{ backgroundImage: `url(${process.env.PUBLIC_URL}/movie.png)` }}
              onClick={() => console.log(`Clicked on movie ${movie.id}`)}
            />
          ))}
        </div>

        {/* Recommended Section */}
        <h2 className="text-white text-4xl font-jeju-hallasan text-center mt-20 mb-10">Recommended</h2>
        <div className="flex flex-wrap justify-center gap-8">
          {movieCovers.slice(0, window.innerWidth < 768 ? 3 : 4).map((movie) => (
            <div
              key={movie.id}
              className="w-48 h-72 bg-cover bg-center cursor-pointer"
              style={{ backgroundImage: `url(${process.env.PUBLIC_URL}/movie.png)` }}
              onClick={() => console.log(`Clicked on movie ${movie.id}`)}
            />
          ))}
        </div>

        {/* Genres Section */}
        <h2 className="text-white text-4xl font-jeju-hallasan text-center mt-32 mb-10">Genres</h2>
        <div className="flex flex-wrap justify-center gap-8">
          {genres.map((genre, index) => (
            <button
              key={genre.id}
              className={`w-64 h-24 bg-[#140E30] rounded-lg flex items-center justify-center text-white font-kavivanar text-xl hover:bg-[#237ED7] transition-colors ${
                index >= 4 && window.innerWidth >= 768 ? 'mt-8' : ''
              }`}
              onClick={() => console.log(`Clicked on ${genre.title}`)}
            >
              <img src={genre.icon} alt={genre.title} className="w-8 h-8 mr-2" />
              {genre.title}
            </button>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className="relative w-full min-h-screen bg-[#2C2C5D]">
      {/* Navbar */}
      <div className="fixed top-0 left-0 w-full h-24 bg-gradient-to-r from-[#140D31] via-[#1D1739] to-[#2C2C5D] flex items-center justify-between px-8 z-50">
        {/* Logo */}
        <div
          className="w-48 h-24 bg-cover bg-center"
          style={{ backgroundImage: `url(${process.env.PUBLIC_URL}/logo.png)` }}
        />

        {/* Search Bar */}
        {isSearchVisible ? (
          <div className="w-1/3 h-12 bg-gray-200 rounded-full flex items-center px-4">
            <input
              type="text"
              placeholder="Search..."
              className="flex-1 bg-transparent outline-none"
            />
            <button
              className="w-8 h-8 bg-cover bg-center"
              style={{ backgroundImage: `url(${process.env.PUBLIC_URL}/search-icon.png)` }}
            />
          </div>
        ) : (
          <div className="absolute top-24 left-0 w-full h-12 bg-gray-200 flex items-center px-4 rounded-b-full">
            <input
              type="text"
              placeholder="Search..."
              className="flex-1 bg-transparent outline-none"
            />
            <button
              className="w-8 h-8 bg-cover bg-center"
              style={{ backgroundImage: `url(${process.env.PUBLIC_URL}/search-icon.png)` }}
            />
          </div>
        )}

        {/* Sign Up & Sign In Buttons */}
        <div className={`flex ${isSearchVisible ? 'gap-4' : 'flex-col gap-2'}`}>
          <button
            className="w-24 h-10 bg-white text-[#237ED7] font-bold rounded-lg hover:bg-[#237ED7] hover:text-white transition-colors"
            onClick={() => navigate('/signup')}
          >
            Sign Up
          </button>
          <button
            className="w-24 h-10 bg-white text-[#237ED7] font-bold rounded-lg hover:bg-[#237ED7] hover:text-white transition-colors"
            onClick={() => navigate('/login')}
          >
            Sign In
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="pt-32">
        <MainContent />
      </div>

      {/* Footer */}
      <div className="fixed bottom-0 left-0 w-full h-16 bg-[#2C2C5D] flex items-center justify-center gap-12">
        <button className="text-white text-xl font-iceberg hover:text-[#237ED7] transition-colors">
          HELP CENTER
        </button>
        <button className="text-white text-xl font-iceberg hover:text-[#237ED7] transition-colors">
          ABOUT US
        </button>
        <button className="text-white text-xl font-iceberg hover:text-[#237ED7] transition-colors">
          WAYS TO WATCH
        </button>
        <button className="text-white text-xl font-iceberg hover:text-[#237ED7] transition-colors">
          CONTACT US
        </button>
      </div>
    </div>
  );
}

export default Main;
