import React from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

function SignUpPage() {
  const navigate = useNavigate();

  const handleSignUp = () => {
    alert('Sign Up button clicked!');
  };

  const handleBarsIcon = () => {
    alert('Bars icon clicked!');
  };

  return (
    <div className="container-fluid vh-100 position-relative" style={{ backgroundColor: '#FFFFFF' }}>
      {/* مستطیل طوسی رنگ (برای صفحه‌های بزرگ‌تر از ۴۵۰ پیکسل) */}
      <div className="d-none d-md-block position-absolute top-50 start-50 translate-middle w-70 h-500px bg-gradient rounded-3" style={{ background: 'linear-gradient(180deg, #D9D9D9 0%, #737373 100%)' }}></div>

      {/* محتوای داخل مستطیل طوسی */}
      <div className="position-absolute top-50 start-50 translate-middle w-80">
        {/* فیلد Email Address */}
        <div className="mb-4">
          <input
            type="text"
            placeholder="Email Address"
            className="form-control w-100 p-3 rounded-3"
            style={{ fontFamily: 'Inspiration', fontSize: '22px', color: '#999999', border: '1px solid #ccc' }}
          />
        </div>

        {/* فیلد Username */}
        <div className="mb-4">
          <input
            type="text"
            placeholder="Username"
            className="form-control w-100 p-3 rounded-3"
            style={{ fontFamily: 'Inspiration', fontSize: '22px', color: '#999999', border: '1px solid #ccc' }}
          />
        </div>

        {/* فیلد Password */}
        <div className="mb-4">
          <input
            type="password"
            placeholder="Password"
            className="form-control w-100 p-3 rounded-3"
            style={{ fontFamily: 'Inspiration', fontSize: '22px', color: '#999999', border: '1px solid #ccc' }}
          />
        </div>

        {/* دکمه SIGN UP */}
        <div className="d-flex justify-content-center mb-4">
          <button
            className="btn btn-primary w-25 p-3 rounded-3"
            onClick={handleSignUp}
            style={{ fontFamily: 'Inter', fontWeight: 800, fontSize: '24px' }}
          >
            SIGN UP
          </button>
        </div>

        {/* متن Already have account?! */}
        <div className="text-center">
          <span
            className="text-primary"
            style={{ fontFamily: 'Inter', fontStyle: 'italic', fontWeight: 800, fontSize: '25px', cursor: 'pointer' }}
            onClick={() => navigate('/login')}
          >
            Already have account?!
          </span>
        </div>
      </div>

      {/* آیکون home */}
      <div
        className="position-absolute top-20 start-20"
        style={{ width: '50px', height: '50px', cursor: 'pointer' }}
        onClick={() => navigate('/')}
      >
        <img
          src={`${process.env.PUBLIC_URL}/home-icon.png`}
          alt="Home"
          className="w-100 h-100"
        />
      </div>

      {/* آیکون bars */}
      <div
        className="position-absolute top-20 end-20"
        style={{ width: '30px', height: '30px', cursor: 'pointer' }}
        onClick={handleBarsIcon}
      >
        <img
          src={`${process.env.PUBLIC_URL}/bars-icon.png`}
          alt="Bars"
          className="w-100 h-100"
        />
      </div>
    </div>
  );
}

export default SignUpPage;
