// src/components/Main.js
import React from 'react';
import { useNavigate } from 'react-router-dom';

function Main() {
  const navigate = useNavigate();

  const styles = {
    mainContainer: {
      position: 'relative',
      width: '100%',
      height: '4100px',
      background: '#2C2C5D',
    },
    navbar: {
      position: 'absolute',
      width: '1441px',
      height: '200px',
      left: 'calc(50% - 1441px/2 - 0px)',
      top: '0px',
      background: 'linear-gradient(90deg, #140D31 32.5%, rgba(29, 23, 57, 0.96) 51%, #2C2C5D 90%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      padding: '0 20px',
    },

    // لوگو
    logo: {
      width: '313px',
      height: '200px',
      background: 'url(logo.jpg)',
      backgroundSize: 'cover',
    },

    // سرچ بار
    searchBar: {
      boxSizing: 'border-box',
      position: 'absolute',
      width: '434px',
      height: '57px',
      left: '469px',
      top: 'calc(50% - 57px/2 - 0.5px)',
      background: '#D9D9D9',
      border: '1px solid #D9D9D9',
      borderRadius: '30px',
      display: 'flex',
      alignItems: 'center',
      padding: '0 10px',
    },

    // آیکون سرچ
    searchIcon: {
      position: 'absolute',
      height: '35px',
      left: '88.02%',
      right: '3.23%',
      top: 'calc(50% - 35px/2)',
      cursor: 'pointer',
    },

    // دکمه‌های Sign Up و Sign In
    buttons: {
      position: 'absolute',
      width: '465px',
      height: '57px',
      right: '38px',
      top: 'calc(50% - 57px/2 + 4.5px)',
      display: 'flex',
      gap: '10px',
    },

    // دکمه Sign Up
    signUp: {
      boxSizing: 'border-box',
      position: 'absolute',
      width: '97px',
      height: '36px',
      left: '368px',
      top: '10px',
      background: '#FFFFFF',
      border: '1px solid #FFFFFF',
      borderRadius: '10px',
      fontFamily: 'Inter',
      fontWeight: '800',
      fontSize: '15px',
      cursor: 'pointer',
    },

    // دکمه Sign In
    signIn: {
      boxSizing: 'border-box',
      position: 'absolute',
      width: '97px',
      height: '36px',
      left: '243px',
      top: '10px',
      background: '#FFFFFF',
      border: '1px solid #FFFFFF',
      borderRadius: '10px',
      fontFamily: 'Inter',
      fontWeight: '800',
      fontSize: '15px',
      color: '#237ED7',
      cursor: 'pointer',
    },

    // محتوای اصلی
    mainContent: {
      position: 'absolute',
      width: '100%',
      top: '200px', // زیر نوبار
    },

    // فوتر
    footer: {
      position: 'absolute',
      width: '1173px',
      height: '36px',
      left: 'calc(50% - 1173px/2 - 0.5px)',
      bottom: '89px',
      display: 'flex',
      justifyContent: 'center',
      gap: '20px',
    },

    // دکمه‌های فوتر
    footerButton: {
      fontFamily: 'Iceberg',
      fontStyle: 'normal',
      fontWeight: '400',
      fontSize: '25px',
      lineHeight: '30px',
      color: '#FFFFFF',
      background: 'none',
      border: 'none',
      cursor: 'pointer',
    },
  };

  return (
    <div style={styles.mainContainer}>
      {/* نوبار */}
      <div style={styles.navbar}>
        {/* لوگو */}
        <div style={styles.logo}></div>

        {/* سرچ بار */}
        <div style={styles.searchBar}>
          <input type="text" placeholder="Search..." style={{ flex: 1, border: 'none', background: 'transparent', outline: 'none' }} />
          <button style={styles.searchIcon}></button>
        </div>

        {/* دکمه‌های Sign Up و Sign In */}
        <div style={styles.buttons}>
          <button style={styles.signUp}>Sign Up</button>
          <button style={styles.signIn} onClick={() => navigate('/login')}>
            Sign In
          </button>
        </div>
      </div>

      {/* محتوای اصلی */}
      <div style={styles.mainContent}>
        {/* محتوای اصلی اینجا قرار می‌گیره */}
      </div>

      {/* فوتر */}
      <div style={styles.footer}>
        <button style={styles.footerButton}>HELP CENTER</button>
        <button style={styles.footerButton}>ABOUT US</button>
        <button style={styles.footerButton}>WAYS TO WATCH</button>
        <button style={styles.footerButton}>CONTACT US</button>
      </div>
    </div>
  );
}

export default Main;
