import React from 'react';
import { useNavigate } from 'react-router-dom';

function Main() {
  const navigate = useNavigate();

  const styles = {
    mainContainer: {
      position: 'relative',
      width: '100%',
      height: '4100px',
      background: '#2C2C5D',
    },
    navbar: {
      position: 'absolute',
      width: '99%',
      height: '150px',
      left: 'calc(50% - 1441px/2 - 0px)',
      top: '0px',
      background: 'linear-gradient(90deg, #140D31 32.5%, rgba(29, 23, 57, 0.96) 51%, #2C2C5D 90%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      padding: '0 20px',
    },
    logo: {
      position: 'absolute',
      width: '18%',
      height: '150px',
      left: '7%',
      top: '0%',
      background: `url(${process.env.PUBLIC_URL}/logo.png)`,
      backgroundSize: 'cover',
    },
    searchBar: {
      boxSizing: 'border-box',
      position: 'absolute',
      width: '30%',
      height: '50px',
      left: '36%',
      top: 'calc(50% - 57px/2 - 0.5px)',
      background: '#D9D9D9',
      border: '1px solid #D9D9D9',
      borderRadius: '30px',
      display: 'flex',
      alignItems: 'center',
      padding: '0 10px',
    },
    searchIcon: {
      position: 'absolute',
      height: '35px',
      left: '88.02%',
      right: '3.23%',
      top: 'calc(50% - 35px/2)',
      background: `url(${process.env.PUBLIC_URL}/search-icon.png)`,
      cursor: 'pointer',
      border: 'none',
      boxShadow: 'none',
    },
    buttons: {
      position: 'absolute',
      width: '465px',
      height: '57px',
      left: '62%',
      top: 'calc(50% - 57px/2 + 4.5px)',
      display: 'flex',
      gap: '10px',
    },
    signUp: {
      boxSizing: 'border-box',
      position: 'absolute',
      width: '97px',
      height: '36px',
      left: '368px',
      top: '10px',
      background: '#FFFFFF',
      border: '1px solid #FFFFFF',
      borderRadius: '10px',
      fontFamily: 'Inter',
      fontWeight: '800',
      fontSize: '17px',
      color: '#237ED7',
      cursor: 'pointer',
    },
    signIn: {
      boxSizing: 'border-box',
      position: 'absolute',
      width: '97px',
      height: '36px',
      left: '243px',
      top: '10px',
      background: '#FFFFFF',
      border: '1px solid #FFFFFF',
      borderRadius: '10px',
      fontFamily: 'Inter',
      fontWeight: '800',
      fontSize: '17px',
      color: '#237ED7',
      cursor: 'pointer',
    },
    mainContent: {
      position: 'absolute',
      width: '100%',
      top: '200px',
    },
    footer: {
      position: 'absolute',
      width: '99%',
      height: '36px',
      left: '1%',
      bottom: '3%',
      display: 'flex',
      justifyContent: 'center',
      gap: '9%',
    },
    footerButton: {
      fontFamily: 'Iceberg',
      fontStyle: 'normal',
      fontWeight: '400',
      fontSize: '25px',
      lineHeight: '30px',
      color: '#FFFFFF',
      background: 'none',
      border: 'none',
      cursor: 'pointer',
    },
  };

  const MainContent = () => {
    const contentStyles = {
      groupContainer: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-between',
        margin: '5%',
      },
      rectangleButton: {
        width: '263px',
        height: '97px',
        background: '#140E30',
        borderRadius: '15px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        cursor: 'pointer',
        border: 'none',
        color: '#FFFFFF',
        fontFamily: 'Kavivanar',
        fontSize: '24px',
        fontWeight: '400',
        margin: '10px',
      },
      movieCover: {
        width: '200px',
        height: '300px',
        backgroundSize: 'cover',
        cursor: 'pointer',
        margin: '10px',
        border: 'none',
      },
      sectionTitle: {
        fontFamily: 'JejuHallasan',
        fontSize: '45px',
        color: '#FFFFFF',
        textAlign: 'center',
        marginTop: '8%',
      },
    };

    const movieCovers = [
      { id: 1, image: 'MV5BYTJkNGQyZDgtZDQ0NC00MDM0LWEzZWQtYzUzZDEwMDljZWNjXkEyXkFqcGc@._V1_.jpg' },
      { id: 2, image: 'MV5BYTJkNGQyZDgtZDQ0NC00MDM0LWEzZWQtYzUzZDEwMDljZWNjXkEyXkFqcGc@._V1_.jpg' },
      { id: 3, image: 'MV5BYTJkNGQyZDgtZDQ0NC00MDM0LWEzZWQtYzUzZDEwMDljZWNjXkEyXkFqcGc@._V1_.jpg' },
      { id: 4, image: 'MV5BYTJkNGQyZDgtZDQ0NC00MDM0LWEzZWQtYzUzZDEwMDljZWNjXkEyXkFqcGc@._V1_.jpg' },
    ];

    const genres = [
        { id: 1, title: 'Film', icon: `${process.env.PUBLIC_URL}/icons8-film-reel-30 1.png` },
        { id: 2, title: 'Series', icon: `${process.env.PUBLIC_URL}/icons8-xbox-series-x-30 1.png` },
        { id: 3, title: 'Reality', icon: `${process.env.PUBLIC_URL}/icons8-cinema-24(2) 1.png` },
        { id: 4, title: 'Games', icon: `${process.env.PUBLIC_URL}/icons8-game-30 1.png` },
        { id: 5, title: 'Documentary', icon: `${process.env.PUBLIC_URL}/icons8-documentary-30 1.png` },
        { id: 6, title: 'Stand-up Comedy', icon: `${process.env.PUBLIC_URL}/icons8-mic-30 1.png` },
    ];

    return (
      <div>
        {/* trending */}
        <h2 style={contentStyles.sectionTitle}>Trending</h2>
        <div style={contentStyles.groupContainer}>
          {movieCovers.map((movie) => (
            <button
              key={movie.id}
              style={{
                ...contentStyles.movieCover,
                backgroundImage: `url(${process.env.PUBLIC_URL}/movie.png)`,
              }}
              onClick={() => console.log(`Clicked on movie ${movie.id}`)}
            />
          ))}
        </div>

        {/* most viewed */}
        <h2 style={contentStyles.sectionTitle}>Most Viewed</h2>
        <div style={contentStyles.groupContainer}>
          {movieCovers.map((movie) => (
            <button
              key={movie.id}
              style={{
                ...contentStyles.movieCover,
                backgroundImage: `url(${process.env.PUBLIC_URL}/movie.png)`,
              }}
              onClick={() => console.log(`Clicked on movie ${movie.id}`)}
            />
          ))}
        </div>

        {/* recommended */} 
        <h2 style={contentStyles.sectionTitle}>Recommended</h2>
        <div style={contentStyles.groupContainer}>
          {movieCovers.map((movie) => (
            <button
              key={movie.id}
              style={{
                ...contentStyles.movieCover,
                backgroundImage: `url(${process.env.PUBLIC_URL}/movie.png)`,
              }}
              onClick={() => console.log(`Clicked on movie ${movie.id}`)}
            />
          ))}
        </div>

        {/* genres */}
        <h2 style={contentStyles.sectionTitle}>Genres</h2>
        <div style={contentStyles.groupContainer}>
          {genres.map((genre) => (
            <button
              key={genre.id}
              style={contentStyles.rectangleButton}
              onClick={() => console.log(`Clicked on ${genre.title}`)}
            >
              <img
                src={genre.icon}
                alt={genre.title}
                style={{ width: '30px', height: '30px', marginRight: '10px' }}
              />
              {genre.title}
            </button>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div style={styles.mainContainer}>
      {/* navbar */}
      <div style={styles.navbar}>
        {/* logo */}
        <div style={styles.logo}></div>

        {/* search bar */}
        <div style={styles.searchBar}>
          <input type="text" placeholder="Search..." style={{ flex: 1, border: 'none', background: 'transparent', outline: 'none' }} />
          <button style={styles.searchIcon}></button>
        </div>

        {/*  Sign Up - Sign In */}
        <div style={styles.buttons}>
          <button style={styles.signUp} onClick={() => navigate('/signup')}>
            Sign Up
          </button>
          <button style={styles.signIn} onClick={() => navigate('/login')}>
            Sign In
          </button>
        </div>
      </div>

      {/* */}
      <div style={styles.mainContent}>
        <MainContent />
      </div>

      {/* footer */}
      <div style={styles.footer}>
        <button style={styles.footerButton}>HELP CENTER</button>
        <button style={styles.footerButton}>ABOUT US</button>
        <button style={styles.footerButton}>WAYS TO WATCH</button>
        <button style={styles.footerButton}>CONTACT US</button>
      </div>
    </div>
  );
}

export default Main;
