import React from 'react';
import { useNavigate } from 'react-router-dom';

function LoginPage() {
  const navigate = useNavigate();

  const handleLogin = () => {
    alert('Login button clicked!');
  };

  const handleForgotPassword = () => {
    alert('Forgot password clicked!');
  };

  const handleBarsIcon = () => {
    alert('Bars icon clicked!');
  };

  return (
    <div className="min-h-screen bg-white relative">
      {/*  */}
      <div className="hidden md:block absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-[700px] h-[500px] bg-gradient-to-b from-gray-300 to-gray-600 rounded-3xl"></div>

      {/*  */}
      <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-[80%]">
        {/* username */}
        <div className="mb-6">
          <input
            type="text"
            placeholder="username"
            className="w-full p-4 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
            style={{ fontFamily: 'Inspiration', fontSize: '22px', color: '#999999' }}
          />
        </div>

        {/* password */}
        <div className="mb-6">
          <input
            type="password"
            placeholder="password"
            className="w-full p-4 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
            style={{ fontFamily: 'Inspiration', fontSize: '22px', color: '#999999' }}
          />
        </div>

        {/* LOG IN */}
        <div className="flex justify-center mb-6">
          <button
            className="w-[25%] p-4 bg-blue-600 text-white rounded-xl font-bold text-2xl hover:bg-blue-700 transition-colors"
            onClick={handleLogin}
          >
            LOG IN
          </button>
        </div>

        {/* Forgot password? */}
        <div className="text-center mb-6">
          <span
            className="text-blue-600 italic font-bold text-2xl cursor-pointer hover:text-blue-700 transition-colors"
            onClick={handleForgotPassword}
          >
            Forgot password?
          </span>
        </div>

        {/* No account? Create one */}
        <div className="text-center">
          <span
            className="text-white italic font-bold text-2xl"
          >
            No account?
          </span>
          <span
            className="text-blue-600 italic font-bold text-2xl cursor-pointer hover:text-blue-700 transition-colors ml-2"
            onClick={() => navigate('/signup')}
          >
            Create one
          </span>
        </div>
      </div>

      {/* home */}
      <div
        className="absolute top-5 left-5 w-12 h-12 cursor-pointer"
        onClick={() => navigate('/')}
      >
        <img
          src={`${process.env.PUBLIC_URL}/home-icon.png`}
          alt="Home"
          className="w-full h-full"
        />
      </div>

      {/* bars */}
      <div
        className="absolute top-5 right-5 w-8 h-8 cursor-pointer"
        onClick={handleBarsIcon}
      >
        <img
          src={`${process.env.PUBLIC_URL}/bars-icon.png`}
          alt="Bars"
          className="w-full h-full"
        />
      </div>
    </div>
  );
}

export default LoginPage;
