import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function SignUpPage() {
  const navigate = useNavigate();
  const [backgroundColor, setBackgroundColor] = useState('bg-white');

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 450) {
        setBackgroundColor('bg-gradient-to-b from-gray-300 to-gray-600');
      } else {
        setBackgroundColor('bg-white');
      }
    };

    // Add event listener for window resize
    window.addEventListener('resize', handleResize);

    // Call handleResize initially to set the correct background color
    handleResize();

    // Cleanup event listener on component unmount
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleSignUp = () => {
    alert('Sign Up button clicked!');
  };

  const handleBarsIcon = () => {
    alert('Bars icon clicked!');
  };

  return (
    <div className={`min-h-screen ${backgroundColor} relative`}>
      {/* مستطیل طوسی رنگ (برای صفحه‌های بزرگ‌تر از 450 پیکسل) */}
      <div className="hidden md:block absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-[700px] h-[500px] bg-gradient-to-b from-gray-300 to-gray-600 rounded-3xl"></div>

      {/* محتوای داخل مستطیل طوسی */}
      <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-[80%]">
        {/* Email Address */}
        <div className="mb-6">
          <input
            type="text"
            placeholder="Email Address"
            className="w-full p-4 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
            style={{ fontFamily: 'Inspiration', fontSize: '22px', color: '#999999' }}
          />
        </div>

        {/* Username */}
        <div className="mb-6">
          <input
            type="text"
            placeholder="Username"
            className="w-full p-4 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
            style={{ fontFamily: 'Inspiration', fontSize: '22px', color: '#999999' }}
          />
        </div>

        {/* Password */}
        <div className="mb-6">
          <input
            type="password"
            placeholder="Password"
            className="w-full p-4 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
            style={{ fontFamily: 'Inspiration', fontSize: '22px', color: '#999999' }}
          />
        </div>

        {/* SIGN UP */}
        <div className="flex justify-center mb-6">
          <button
            className="w-[25%] p-4 bg-blue-600 text-white rounded-xl font-bold text-2xl hover:bg-blue-700 transition-colors"
            onClick={handleSignUp}
          >
            SIGN UP
          </button>
        </div>

        {/* Already have account?! */}
        <div className="text-center">
          <span
            className="text-blue-600 italic font-bold text-2xl cursor-pointer hover:text-blue-700 transition-colors"
            onClick={() => navigate('/login')}
          >
            Already have account?!
          </span>
        </div>
      </div>

      {/* home */}
      <div
        className="absolute top-5 left-5 w-12 h-12 cursor-pointer"
        onClick={() => navigate('/')}
      >
        <img
          src={`${process.env.PUBLIC_URL}/home-icon.png`}
          alt="Home"
          className="w-full h-full"
        />
      </div>

      {/* bars */}
      <div
        className="absolute top-5 right-5 w-8 h-8 cursor-pointer"
        onClick={handleBarsIcon}
      >
        <img
          src={`${process.env.PUBLIC_URL}/bars-icon.png`}
          alt="Bars"
          className="w-full h-full"
        />
      </div>
    </div>
  );
}

export default SignUpPage;
